html, body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Helvetica Neue", Arial, sans-serif;
}

h1 {
  font-size: 2em;
  font-weight: 500;
  line-height: 1.6em;
  padding: 0.8em 0 0 0;
}

h2 {
  font-size: 1.1em;
  font-weight: 500;
  line-height: 1.4em;
  padding: 0.9em 0 0 0;
  margin: 0 0 0.9em 0;
}

h3 {
  font-size: 1em;
  font-weight: 400;
  line-height: 1.6em;
}

p {
  font-size: 1em;
  font-weight: 400;
  line-height: 1.6em;
  padding: 0;
  margin: 0.5em 0;
}

a {
  text-decoration: none;
  color: #000;
}

p > a {
  color: #785EF0;
  text-decoration: none;
}

p > a:hover {
  text-decoration: underline;
}

ul {
  list-style-type: none;
  margin: 0;
  padding: 0;
}

.grid > * {
  padding: 1em;
  margin: 0 0 1em 0;
}

/* blur */
.blur_blur-active {
  filter: blur(5px);
  transition: all 0.1s;
}

/* freeze */
.body_freeze-active {
  overflow-y: scroll;
  width: 100%;
}

/* modal */
.modal {
  display: block;
  position: fixed;
  z-index: 1;
  padding-top: 1em;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.4);
  opacity: 0;
  visibility: hidden;
  transition: all 0.1s;
}

.modal_visible {
  opacity: 1;
  visibility: visible;
}

.modal__content {
  background-color: #fff;
  margin: auto;
  padding: 0em 2em 1em 2em;
  width: calc(100% - 6em);
  max-height: calc(100% - 3em);
  max-width: 40em;
  overflow: scroll;
}

.modal__content::-webkit-scrollbar {
  width: 0px;
  background: transparent;
}

.modal__hamburger-container {
  height: 5em;
  position: sticky;
  position: -webkit-sticky;
  top: 0;
  background-color: #fff;
}

.modal__hamburger_position {
  margin-top: 0.8em;
  right: 0;
}

/* geoLocation & positionPushing & countryLinking */
.modal__geoLocation-lists, .modal__positionPushing-lists, .modal__countryAlternative-lists {
  display: none;
}

.modal__geoLocation-lists_visible, .modal__positionPushing-lists_visible, .modal__countryAlternative-lists_visible {
  display: block;
}

.modal__geoLocation-list, .modal__positionPushing-list, .modal__countryAlternative-list {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(12em, 1fr));
  grid-gap: 1em 2em;
  list-style-type: none;
  margin-bottom: 2em;
}

.modal__geoLocation-introduction, .modal__positionPushing-introduction, .modal__countryAlternative-introduction {
  grid-row-start: span 4;
  border-top: rgba(0, 0, 0, 0.4) 2px solid;
}

.modal__geoLocation-list-item, .modal__positionPushing-list-item, .modal__countryAlternative-list-item {
  border-top: rgba(0, 0, 0, 0.14) 1px solid;
  padding: 1em 0em;
  display: flex;
  align-items: center;
}

.modal__geoLocation-item-img, .modal__positionPushing-item-img, .modal__countryAlternative-item-img {
  margin-right: 1em;
  height: 1.5em;
  width: 1.5em;
  display: inline-block;
}

.modal__about {
  border-top: rgba(0, 0, 0, 0.4) 2px solid;
  display: none;
  margin-bottom: 3em;
}

.modal__about-img {
  float: right;
  width: 56px;
  height: 56px;
  margin: 1em 2em 1em 1em;
}

.modal__about_visible {
  display: block;
}

.modal__reportProblem {
  display: none;
}

.modal__reportProblem_visible {
  display: block;
}

.modal__reportProblem-header-text {
  border-top: rgba(0, 0, 0, 0.4) 2px solid;
}

.modal__reportProblem-description {
  margin-top: 2em;
  display: flex;
}

.modal__reportProblem-description_invisible {
  display: none;
}

.modal__reportProblem-description-text {
  padding: 0.75em 1em;
  border-radius: 4px;
  border: rgba(0, 0, 0, 0.14) 1px solid;
  width: 50%;
  max-width: 15em;
}

.modal__reportProblem-description-text:focus {
  outline: none;
}

.modal__reportProblem-description-button {
  border: 1px solid #648FFF;
  margin-left: 1em;
  padding: 0.75em 1em;
  background-color: #648FFF;
  border-radius: 4px;
  color: #fff;
  width: 50%;
  max-width: 9em;
  cursor: pointer;
}

/** navigation **/
.nav {
  border-bottom: rgba(0, 0, 0, 0.14) 1px solid;
  height: 3em;
  display: flex;
  align-items: center;
}

.nav__header-link {
  margin: 0 0 0 5%;
}

.nav__logo-img {
  display: inline-block;
  height: 3em;
  width: 3em;
  cursor: pointer;
}

.nav__header-text {
  display: inline-block;
  font-size: 1.7em;
  font-weight: 500;
  padding: 0;
  margin: 0;
  cursor: pointer;
}

.nav__lang-country-flag {
  cursor: pointer;
  width: 2em;
  height: 2em;
  position: absolute;
  right: 5.5em;
}

.nav__hamburger_position {
  right: 1.5em;
}

@media screen and (min-width: 93em) {
  .nav__logo-link {
    margin-left: calc(10% + 1em);
  }

  .nav__lang-country-flag {
    right: calc(10% + 6em);
  }

  .nav__hamburger_position {
    right: calc(10% + 1.5em);
  }
}
/** header section **/
.header__header-text {
  border-top: rgba(0, 0, 0, 0.4) 2px solid;
  overflow-wrap: break-word;
}

.header__mark {
  background-color: transparent;
  border-radius: 4px;
  padding: 0 0.3em 0.2em 0.3em;
  box-decoration-break: clone;
  -webkit-box-decoration-break: clone;
}

.header__mark_green {
  background-color: rgba(0, 181, 63, 0.2);
}

.header__mark_orange {
  background-color: rgba(255, 102, 0, 0.2);
}

.header__mark_red {
  background-color: rgba(242, 68, 68, 0.2);
}

/** faq section **/
.faq__header-text {
  border-top: rgba(0, 0, 0, 0.4) 2px solid;
}

.faq__accordion {
  width: 100%;
  border-top: rgba(0, 0, 0, 0.14) 1px solid;
  border-right: 0px;
  border-bottom: 0px;
  border-left: 0px;
  background-color: #fff;
  cursor: pointer;
  padding: 0.75em 0;
  text-align: left;
  outline: none;
}

.faq__accordion-header-text {
  margin: 0;
}

.faq__accordion-header-text::after {
  content: "+";
  color: rgba(0, 0, 0, 0.4);
  font-weight: bold;
  float: right;
  margin: 0 10px;
}

.faq__accordion-header-text_open::after {
  content: "−";
}

/* Panel height must be adjusted by adding a class. */
.faq__panel {
  padding: 0;
  background-color: #fff;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.2s ease-out;
}

/** about **/
.about__header-text {
  border-top: rgba(0, 0, 0, 0.4) 2px solid;
}

.breadcrumb {
  padding: 0;
  margin: 0.75em 0 0 0;
  list-style: none;
}

.breadcrumb__crumb {
  display: inline;
}

.breadcrumb__crumb + .breadcrumb__crumb:before {
  padding: 0 0.5em;
  color: #000;
  content: "/ ";
}

.breadcrumb__crumb-link {
  color: #785EF0;
  text-decoration: none;
}

.breadcrumb__crumb-link:hover {
  text-decoration: underline;
}

.breadcrumb__crumb-link_black-dec {
  color: #000;
}

.breadcrumb__crumb-link_black-dec:hover {
  text-decoration: none;
  cursor: default;
}

/** down grid **/
.serviceDownGrid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(13em, 1fr));
  grid-gap: 2em 1em;
}

.serviceDownGrid__container {
  border-top: rgba(0, 0, 0, 0.4) 2px solid;
}

.serviceDownGrid__line-up {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}

.serviceDownGrid__header-text {
  cursor: pointer;
  display: inline-block;
}

.serviceDownGrid__pulse {
  display: inline-block;
  border-radius: 50%;
  cursor: pointer;
  width: 0.4em;
  height: 0.4em;
  margin: 0 1em;
}

.serviceDownGrid__pulse_red {
  border: 2px solid #f24444;
  animation: red-pulse 1.7s infinite;
  background-color: #f24444;
}

.serviceDownGrid__pulse_orange {
  border: 2px solid #FF6600;
  animation: orange-pulse 1.7s infinite;
  background-color: #FF6600;
}

.serviceDownGrid__pulse_green {
  border: 2px solid #00b53f;
  animation: green-pulse 1.7s infinite;
  background-color: #00b53f;
}

@keyframes red-pulse {
  0% {
    box-shadow: 0 0 0 0 rgba(242, 68, 68, 0.5);
  }
  65% {
    box-shadow: 0 0 0 10px rgba(242, 68, 68, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(242, 68, 68, 0);
  }
}
@keyframes orange-pulse {
  0% {
    box-shadow: 0 0 0 0 rgba(255, 102, 0, 0.5);
  }
  65% {
    box-shadow: 0 0 0 10px rgba(255, 102, 0, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(255, 102, 0, 0);
  }
}
@keyframes green-pulse {
  0% {
    box-shadow: 0 0 0 0 rgba(0, 181, 63, 0.5);
  }
  65% {
    box-shadow: 0 0 0 10px rgba(0, 181, 63, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(0, 181, 63, 0);
  }
}
.serviceDownGrid__img-container {
  height: 6.25em;
  display: flex;
  align-items: center;
}

.serviceDownGrid__img {
  width: 100%;
  height: 100%;
  display: block;
  margin: auto;
  cursor: pointer;
}

.serviceDownGrid__text {
  cursor: pointer;
}

.hamburger {
  cursor: pointer;
  position: absolute;
  width: 48px;
  height: 48px;
}

.hamburger__top-bun,
.hamburger__bottom-bun {
  content: "";
  display: block;
  position: absolute;
  left: 15px;
  width: 25px;
  height: 2px;
  background: rgba(0, 0, 0, 0.4);
  transform: rotate(0);
}

.hamburger__top-bun {
  top: 23px;
  transform: translateY(-5px);
}

.hamburger__bottom-bun {
  bottom: 23px;
  transform: translateY(5px);
}

.hamburger_open {
  transform: rotate(90deg);
}

.hamburger__top-bun_open {
  transform: rotate(45deg) translateY(0px);
}

.hamburger__bottom-bun_open {
  transform: rotate(-45deg) translateY(0px);
}

@media screen and (min-width: 65em) {
  .grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
    grid-template-rows: auto;
    grid-template-areas: "nav nav nav nav nav nav " "header header header header header header" "faq faq faq about about about" "dgrid dgrid dgrid dgrid dgrid dgrid";
    grid-gap: 1em;
  }

  .nav {
    grid-area: nav;
  }

  .header {
    grid-area: header;
  }

  .faq {
    grid-area: faq;
  }

  .about {
    grid-area: about;
  }

  .serviceDownGrid {
    grid-area: dgrid;
  }
}
@media screen and (min-width: 80em) {
  .grid {
    display: grid;
    grid-template-columns: 10% 1fr 1fr 1fr 1fr 1fr 1fr 10%;
    grid-template-rows: auto;
    grid-template-areas: "nav nav nav nav nav nav nav nav" ". header header header header header header ." ". faq faq faq about about about ." ". dgrid dgrid dgrid dgrid dgrid dgrid .";
    grid-gap: 1em;
  }
}
